import React from "react";
import getDataFromApi from "../services/api";
import { Route, Switch } from "react-router-dom";
import PersonList from "./PersonList";

class App extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      users: [],
      filterCity: "",
      filterGender: "",
      isLoading: true,
    };
  }

  render() {
    return (
      <>
        <Route path="/" exact>
          <PersonList />
        </Route>
      </>
    );
  }
}

export default App;
